import React, { useState } from "react";
import { sidebarName } from "./constant";

export const SideBar = () => {
  const [activeId, setActiveId] = useState(0);
  return (
    <div>
      <div className="w-[300px] h-screen border-r-[0.05px] border-[#c79427]">
        {sidebarName?.map((i, j) => (
          <>
            <div
              className={`group w-[100%] h-[80px] border-b-[0.05px] border-[#c79427] ${
                activeId === j && "border-b-[2px] shadow-md  "
              } cursor-pointer`}
              onClick={() => setActiveId(j)}
            >
              <div className="w-[100%] flex text-center">
                <img className="pt-[10px]" src={i.img} alt="" />
                <p className="pt-[30px]">{i.name}</p>
              </div>
            </div>
          </>
        ))}
      </div>
    </div>
  );
};
